mapper.select:
Select Count(userId)
From user
where 1=1
	and userId=#{userId}
	and userPass=#{userPass}

mapper.update:
UPDATE product SET hitCnt = hitCnt+1
WHERE 1=1
	AND productNo=#{productNo}

mapper.select:
