mapper.update:
UPDATE estate SET esTradeType =#{esTradeType} , esPrice =#{esPrice}, esPosiType =#{esPosiType}
				, esSize1 =#{esSize1}, esSize2 =#{esSize2}, esOption1 =#{esOption1}, esOption2 =#{esOption2}
				, esOption3 =#{esOption3}, esCostYn =#{esCostYn}, esCost =#{esCost}, esCostChk =#{esCostChk}, esSeperCostChk =#{esSeperCostChk}
				, esCostContent =#{esCostContent}
				,regDate2 =now()
	WHERE 1=1
		AND esNo= (SELECT max(esNo) FROM estate WHERE esWriter = #{esWriter}   )

UPDATE estate SET esLoanYn = #{esLoanYn},esAnimalYn = #{esAnimalYn},esParkingYn = #{esParkingYn}
				 ,esMoveYn = #{esMoveYn},esMoveDate = #{esMoveDate}
				 ,esFacility = #{esFacility},esAdvantage = #{esAdvantage},esHouseIntro = #{esHouseIntro}
				 ,regDate3 =now()
	WHERE 1=1
		AND esNo= (SELECT max(esNo) FROM estate WHERE esWriter = #{esWriter}   )

mapper.select:
SELECT rnum,esNo,esWriter,esHumType,esRoomType,esLoc,esLocDetail,esLocContent,esTradeType,esPrice,esPosiType,esTradeYn, esSize1,esSize2,esOption1,esOption2,esOption3,esCostYn,esCostChk,esSeperCostChk,esCostContent,esLoanYn,esAnimalYn, esParkingYn,esMoveYn,esMoveDate,esFacility,esAdvantage,esHouseIntro,regDate1,regDate2,regDate3,modDate1,modDate2,modDate3, delDate,deleteYn, hitCnt, storedFileName FROM( SELECT a.esNo,a.esWriter,a.esHumType,a.esRoomType,a.esLoc,a.esLocDetail,a.esLocContent,a.esTradeType,a.esPrice,a.esPosiType,a.esTradeYn ,a.esSize1,a.esSize2,a.esOption1,a.esOption2,a.esOption3,a.esCostYn,a.esCost,a.esCostChk,a.esSeperCostChk,a.esCostContent ,a.esLoanYn,a.esAnimalYn,a.esParkingYn,a.esMoveYn,a.esMoveDate,a.esFacility,a.esAdvantage,a.esHouseIntro ,a.regDate1,a.regDate2,a.regDate3,a.modDate1,a.modDate2,a.modDate3,a.delDate,a.deleteYn ,a.hitCnt ,b.storedFileName , ROW_NUMBER() OVER (ORDER BY hitCnt desc) AS rnum FROM estate a INNER JOIN estateFile b ON a.esNo =b.esNo WHERE 1=1
AND esLoc like CONCAT('%',#{esLoc},'%')
)R WHERE 1=1 AND deleteYn='N' GROUP BY esNo ORDER BY hitCnt desc LIMIT 0, #{endRow}

SELECT rnum,carNo,carWriter,carPrice,carTradeYn,carUserId,carType,carManuFacturing,carModel ,carRank,carDetailRank,carDiffDate,carRegDate,carDistance,carDisplaceMent ,carFuel,carTransMission,carAccidentCnt,carSpecialUseYn,carChangeCnt ,carOption1,carOPtion2,carOption3,carAdditionInfo,carIntro,carLoc ,regDate,modDate,delDate,deleteYn,hitCnt ,storedFileName FROM ( SELECT a.carNo,a.carWriter,a.carPrice,a.carTradeYn,a.carUserId,a.carType,a.carManuFacturing,a.carModel ,a.carRank,a.carDetailRank,a.carDiffDate,a.carRegDate,a.carDistance,a.carDisplaceMent ,a.carFuel,a.carTransMission,a.carAccidentCnt,a.carSpecialUseYn,a.carChangeCnt ,a.carOption1,a.carOPtion2,a.carOption3,a.carAdditionInfo,a.carIntro,a.carLoc ,a.regDate,a.modDate,a.delDate,a.deleteYn,a.hitCnt ,b.storedFileName ,ROW_NUMBER() OVER(ORDER BY hitCnt DESC) AS rnum FROM car a INNER JOIN carfile b ON a.carUserId = b.writer WHERE 1=1
AND carLoc like CONCAT('%',#{carLoc},'%')
) R WHERE 1=1 AND deleteYn='N'
GROUP BY carNo ORDER BY hitCnt DESC LIMIT 0, #{endRow}

SELECT rnum,jobNo,jobWriter,jobCompany,jobTitle,jobType,jobMoney,jobLoc,jobLocDetail,jobWorkTime,jobLocContent,hitCnt ,regDate,modDate,delDate,deleteYn,storedFileName FROM ( SELECT a.jobNo,a.jobWriter,a.jobCompany,a.jobTitle,a.jobType,a.jobMoney, a.jobLoc,a.jobLocDetail,a.jobWorkTime,a.jobLocContent,a.hitCnt, a.regDate,a.modDate,a.delDate,a.deleteYn, b.storedFileName, ROW_NUMBER() OVER(ORDER BY hitCnt) AS rnum FROM job a INNER JOIN jobfile b ON a.jobNo =b.jobNo WHERE 1=1
AND a.jobLoc LIKE CONCAT('%',#{jobLoc},'%')
)R WHERE 1=1 AND deleteYn='N' GROUP BY jobNo ORDER BY hitCnt DESC LIMIT 0 ,#{endRow}

 
SELECT busiNoticeNo,busiNo,title,subTitle,content,subContent,regDate,modDate,delDate,deleteYn,HitCnt
FROM busiNotice
WHERE busiNoticeNo=#{busiNoticeNo}
AND deleteYn='N'
limit 0 , #{endRow}

