if.if:
AND ( title like CONCAT('%',#{keyword},'%')
	OR  price <= #{keyword}
	OR  location like CONCAT('%',#{keyword},'%')
	OR  writer like CONCAT('%',#{keyword},'%')
	
		OR type ='sale'
		OR type ='share'
		AND type=#{keyword}
	
	)

AND ( busiNum like CONCAT('%',#{keyword},'%') OR writer like CONCAT('%',#{keyword} ,'%') OR ownerName like CONCAT('%',#{keyword},'%') OR busiName like CONCAT('%',#{keyword},'%')
OR status = 'Y' OR status = 'S' OR status='N'
)

AND ( jobWriter like CONCAT('%',#{keyword},'%')
	OR jobCompany like CONCAT('%',#{keyword},'%')
	OR  jobType = #{keyword}
	OR SUBSTRING(jobLoc,1,6) like CONCAT('%',#{keyword},'%')
)

AND (  title like CONCAT('%',#{keyword},'%')  
    OR content like CONCAT('%',#{keyword},'%')
		)

AND (  title like CONCAT('%',#{keyword},'%')  
    OR content like CONCAT('%',#{keyword},'%')
		)

AND (  title like CONCAT('%',#{keyword},'%')  
    OR content like CONCAT('%',#{keyword},'%')
		)

AND (  
    location like CONCAT('%',#{keyword},'%')  
    OR age=#{keyword} 
    OR gender=#{keyword}
    OR priceType=#{keyword}
    OR price <=#{keyword}  
    OR startType=#{keyword}
    OR days  <=#{keyword}
    OR payStatus=#{keyword}
    OR approveStatus=#{keyword}
		)

AND userId like CONCAT('%',#{keyword},'%')
GROUP BY targetUserNo
HAVING COUNT(case when readYn='N' then 1 END ) >= #{keyword}

mapper.select:
SELECT COUNT(userId)
FROM admin
WHERE 1=1
	AND userId=#{userId}
	AND userPass=#{userPass}

SELECT productFileNo,productNo,productOriginFileName,productStoredFileName,productFileSize,productWriter,regDate,modDate,delDate,deleteYn
FROM productfile
WHERE productNo = #{productNo}

mapper.update:
UPDATE product SET deleteYn ='Y' , delDate =now()
WHERE
productNo =#{productNo}

UPDATE product SET deleteYn ='N' , modDate =now()
WHERE
productNo =#{productNo}

mapper.select:
SELECT idx,userNo,userId,userName,accountNum,tradeBank,saveMoney,moneyType,modDate
FROM useraccount 
WHERE 1=1
AND userNo=#{userNo}
AND accountNum=#{accountNum}

mapper.update:
UPDATE event SET title=#{title},content=#{content}
	WHERE 1=1
	AND eventNo=#{eventNo}

UPDATE event SET deleteYn ='Y' ,delDate =now()
	WHERE 1=1
	AND eventNo=#{eventNo}

UPDATE noticeFile set deleteYn='Y', delDate =now()
wHERE 1=1
	AND fileNo=#{fileNo}

UPDATE notice SET title=#{title},content=#{content},modDate =now()
WHERE 1=1
	AND noticeNo = #{noticeNo}

UPDATE notice SET deleteYn='Y',delDate=now()
WHERE 1=1
	AND noticeNo=#{noticeNo}

UPDATE noticeFile SET deleteYn='Y',delDate=now()
WHERE 1=1
	AND noticeNo=#{noticeNo}

UPDATE inquiry SET status='Y'
WHERE 1=1
AND inquiryNo = #{inquiryNo}

UPDATE answer SET title=#{title}, content=#{content}, modDate=now()
WHERE 1=1
AND inquiryNo=#{inquiryNo}

UPDATE answer SET delDate = now() , deleteYn='Y'
WHERE 1=1
AND inquiryNo=#{inquiryNo}
